import React from 'react'
import styled from 'styled-components'

const iOSShareBase64 =
	''

const closeBase64 =
	'iVBORw0KGgoAAAANSUhEUgAAACAAAAAgCAQAAADZc7J/AAAAl0lEQVR4Ae2TMQ6DMBTFfA+kHqkSUmcKgd5/BIaEqsOfWqyILgx4jewl/3FxSm74q8ODlcQeL1Za769sFNKOXthYaBAS5Wci9ExH4AlG143xKxH6k0oiMbluTEQi9B5BEpW6fNtHH+CfQCEd13PcxVF9IHnC9V6us1qvTsjVScJ1SeicM53MbKZBaFlkcYmZO473aTghF2/X9XbQ39L36gAAAABJRU5ErkJggg=='

const Container = styled('div')`
	position: fixed;
	bottom: 17px;
	left: 5%;
    width: 90%;
	z-index: 9999999;
	background: rgba(255, 255, 255, 0.9);
	padding: 10px;
	padding-top: 25px;
	border-radius: 4px;
	color: #000;
	border: 1px solid rgba(32, 32, 32, 0.1);
	box-shadow: 1px 2px 10px rgba(32, 32, 32, 0.1);

	&:before {
		top: 100%;
		left: 50%;
		border: solid transparent;
		content: ' ';
		height: 0;
		width: 0;
		position: absolute;
		pointer-events: none;
		border-color: rgba(136, 183, 213, 0);
		border-top-color: rgba(255, 255, 255, 0.9);
		border-width: 15px;
		margin-left: -15px;
	}

	.text {
		text-align: center;
		font-size: 12px;
	}
`

const AddToHomeScreen = () => {
	const [visible, setVisible] = React.useState(
		navigator && typeof navigator.standalone !== 'undefined' && navigator.standalone === false
	)

	React.useEffect(() => {
		const listener = e => {
			if (!visible) {
				window.removeEventListener('scroll', listener)
			} else {
				if (window.scrollY > 250) {
					setVisible(false)
				}
			}
		}

		window.addEventListener('scroll', listener)

		return () => window.removeEventListener('scroll', listener)
	}, [visible])

	if (!visible) return null

	return (
		<Container>
			<img
				alt="Add to home screen"
				style={{
					position: 'absolute',
					top: 0,
					right: 0,
					width: 32,
					height: 32,
					padding: 10,
					cursor: 'pointer',
					zIndex: 9999999
				}}
				src={`data:image/png;base64,${closeBase64}`}
				onClick={() => setVisible(false)}
			/>
			<div className="text">
				Add us as an app for quicker access! It's easy, just tap the share button (
				<img
					alt="Add to home screen"
					style={{ verticalAlign: 'top', width: 16, height: 16 }}
					src={`data:image/png;base64,${iOSShareBase64}`}
				/>
				) then find and tap the 'Add to Home Screen' button.
			</div>
		</Container>
	)
}

export default AddToHomeScreen
